import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { User } from '../shared/models/user';
import { NewsletterService } from '../shared/services/newsletter.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
  title = 'app';
  angForm!: FormGroup;
  isEmailDuplicate!: false;
  isSubmitted!: false;

  constructor(
    private formBuilder: FormBuilder,
    private newsletterService: NewsletterService,
  ) {
    this.createForm();
  }

  createForm() {
    this.angForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      contactSource: ['', Validators.required],
      contactReason: ['', Validators.required]
    });
  }

  submitNewsletter(data: { value: { email: string; contactSource: string; contactReason: string; }; }) {
    var user = new User();
    user.email = data.value.email;
    user.contactSource = data.value.contactSource;
    user.contactReason = data.value.contactReason;

    this.newsletterService.submitNewsletter(user)
  }
}
<div>
  <form [formGroup]="angForm" novalidate (ngSubmit)="submitNewsletter(angForm)">
    <div class="form-group">
      <label>Email</label>
      <input class="form-control" formControlName="email" type="email" ngModel email>
    </div>
    <div *ngIf="angForm!.controls['email'].invalid && (angForm!.controls['email'].dirty || angForm!.controls['email'].touched)" class="alert alert-danger">
      <div *ngIf="angForm!.controls['email'].errors.required">
        Email is required.
      </div>
      <div *ngIf="angForm!.controls['email'].errors.email">
        Email is not valid.
      </div>
    </div>
    <div class="form-group">
      <label>How they heard about us</label>
      <select class="form-control" formControlName="contactSource" type="text">
        <option>Advert</option>
        <option>Word of Mouth</option>
        <option>Other</option>
      </select>
    </div>
    <div *ngIf="angForm!.controls['contactSource'].invalid && (angForm!.controls['contactSource'].dirty || angForm!.controls['contactSource'].touched)" class="alert alert-danger">
      <div *ngIf="angForm!.controls['contactSource'].errors.required">
        Contact source is required.
      </div>
    </div>
    <div class="form-group">
      <label>Reason for signing up</label>
      <input class="form-control" formControlName="contactReason" type="text">
    </div>
    <div *ngIf="angForm!.controls['contactReason'].invalid && (angForm!.controls['contactReason'].dirty || angForm!.controls['contactReason'].touched)" class="alert alert-danger">
      <div *ngIf="angForm!.controls['contactReason'].errors.required">
        Contact reason is required.
      </div>
    </div>
    <button type="submit"
            [disabled]="angForm!.pristine || angForm!.invalid" class="btn btn-success">
      Submit
    </button>
  </form>
</div>
